import { VgInput } from 'vg/react'
import 'vg'

function MyComponent() {
  const handleEvent = (event) => {
    console.log('vg-change', event.detail);
  };

  return (
    <VgInput
      label="Username"
      placeholder="Enter your username"
      type="text"
    >
    </VgInput>
  );
}

