import { Component, CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';

@Component({
  selector: 'app-demo',
  standalone: true,
  templateUrl: './demo.component.html',
  schemas: [CUSTOM_ELEMENTS_SCHEMA]
})
export class DemoComponent {
  public label = 'Readonly Field';
  public placeholder = 'This field is disabled';
  public type = 'text';
  public value = 'Cannot edit this';

  onEvent(event: Event) {
    console.log((event as CustomEvent).detail);
  }
}

// demo.component.html
<vg-input
    [label]="label"
    [placeholder]="placeholder"
    [type]="type"
    [value]="value"
    [disabled]="true"
    (vg-change)="onEvent($event)"
>
</vg-input>

sources.lit:
import { LitElement, html } from 'lit';
import { customElement } from 'lit/decorators.js';

@customElement('my-demo')
export class MyDemo extends LitElement {

  handleEvent(e: CustomEvent) {
    console.log(e.type, e.detail);
  }

  render() {
    return html`
      <vg-input
        label="Readonly Field"
        placeholder="This field is disabled"
        type="text"
        value="Cannot edit this"
        ?disabled=${true}
        @vg-change=${this.handleEvent}
      >
      </vg-input>
    `;
  }
}

